import React, { useState, useRef } from 'react'
import styled from 'styled-components'
import Hour from './Hour'

import styles from '../styles/styles.module.css'

const Slider = styled.div`
    width: 100%;
    height: 100%;
    position: relative;
    overflow: hidden;
    display: flex;
    justify-content: space-between;
    margin: 0 1rem;
    transform: ${({slidingAmount}) => `translateX(${slidingAmount}px)`};
    transition: all 1.5s ease-in;
`;

const SliderButton = styled.button`
    width: 50px;
    height: 50px;
    border-radius: 100%;
    text-align: center;
    line-height: 50px;
    z-index: 2;
`;

const DayInfo = ({infos, convertTime}) => {
    const slider =  useRef(null);
    console.log(slider.current.clientWidth)
    const [slidingAmount, setSlidingAmount] = useState(0)
    const slidingRight = () => {
        let va = slidingAmount + slider.current.clientWidth;
        setSlidingAmount(-(slidingAmount = va ))
        console.log(slidingAmount)

    }
    return (
        <div style={{display: 'flex', alignItems: 'center'}}>
            <SliderButton className="leftBtn">L</SliderButton>
            <Slider slidingAmount={slidingAmount} ref={slider}>
            {infos.hour.map(item => 
            <Hour 
                time = {item.time.slice(11, 16)}
                icon ={item.condition.icon} 
                text ={item.condition.text} 
                temp_c ={item.temp_c} 
                convertTime ={convertTime}
            />)}
        </Slider>
            <SliderButton className="rightBtn" onClick={slidingRight}>R</SliderButton>
        </div>
    )
}

export default DayInfo
