import React, {useState,useEffect} from "react";
import axios from 'axios'
import "./style.css";

import Banner from './components/Banner'
import DayInfo from './components/DayInfo'
import WeekInfo from "./components/WeekInfo";
import ExtraDetails from "./components/ExtraDetails";

export default function App() {
  const[info, setInfo] = useState([])

  const getData = async () => {
    const response = await axios.get('https://api.weatherapi.com/v1/forecast.json?key=35360d18e86c4006a60111222212904&q=giza');
    const data = await response.data;
      setInfo(data)
  }
  useEffect(() => {
    getData()
  },[])
  return (
    <div>
      {info.length > 0 ? <div >
      <Banner 
        time ={info.location.localtime.slice(11, 16)}
        name ={info.location.name} 
        temp_c={info.current.temp_c} 
        icon ={info.current.condition.icon} 
        text={info.current.condition.text} />
      <ExtraDetails
        windKPH ={info.condition.wind_kph}
        windDegree ={info.condition.wind_degree}
        windDir ={info.condition.wind_dir}
        pressureIn ={info.condition.pressure_in}
        humidity ={info.condition.humidity}
        cloud ={info.condition.cloud}
        feelsLikeC ={info.condition.feelslike_c}
        visibility ={info.condition.vis_km}
        uv ={info.condition.uv}
        gust ={info.condition.gust_kph}
      />  
      <DayInfo infos={info.forecast.forecastday[0]} />
    </div> : console.log('false')}
    <WeekInfo />
    </div>
  );
}
